INSERT INTO `news_description` (`id`, `title`, `content`, `news_id`, `language_id`) VALUES
(101, 'TRƯỜNG TRUNG HỌC SQUAW VALLEY ACADEMY', '&lt;p align=&quot;center&quot; style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;341&quot; src=&quot;http://farm9.staticflickr.com/8220/8263236540_8a14ba5f01_z.jpg&quot; style=&quot;border: 0px;&quot; width=&quot;455&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p align=&quot;center&quot; style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;strong&gt;1. Giới thiệu chung về trường:&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;strong&gt;&lt;em&gt;Trường trung học Squaw Valley Academy&lt;/em&gt;&lt;/strong&gt; được thành lập năm 1978, đặt tại thành phố Lake Tahoe, bang California, Mỹ Trường&amp;nbsp; gần thành phố: Reno, NV &amp;amp; Sacramento, CA &amp;amp; San Francisco, CA và gần sân bay quốc tế: Reno Tahoe Int’l airport.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;Là một trường phổ thông trung học dạy từ lớp 8 đến lớp 12, chuẩn bị cho học sinh những kiến thức và kỹ năng cần thiết để có thể vào học tại các trường cao đẳng, đại học. Những cơ sở vật chất bao gồm : KTX, phòng học, khu nghệ thuật, phòng xử lí ảnh, phòng thí nghiệm, nhạc, internet...&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;Trường đạt chuẩn của bang California về đào tạo trung học và được Hiệp hội các trường trung học và cao đẳng miền Tây công nhận hoàn toàn. 100% học sinh của trường được nhận vào trung bình là 3 trường cao đẳng hoặc đại học.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;Nhiều học sinh của trường mang quốc tịch Mỹ, bên cạnh đó là các học sinh đến từ rất nhiều quốc gia khác trên thế giới như Đức, Cộng hoà Séc, Áo, Na Uy, Xcốtlen, Nga, Tây Ban Nha, Trung Quốc, Nhật Bản, Hàn Quốc, Đài Loan, Thái Lan, Việt Nam, Inđônêsia, Ai Cập, Canada, Mêhicô, Braxin.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;strong&gt;&lt;em&gt;2.&lt;/em&gt;&lt;/strong&gt;&lt;strong&gt;Tại sao nên học tại Squaw Valley Academy?&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;Vì Squaw Valley có những tiêu chí sau:&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; margin-left: 21pt;&quot;&gt;-&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; THỬ THÁCH bằng đội ngũ giáo viên nghiêm khắc&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; margin-left: 21pt;&quot;&gt;-&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; KHUYẾN KHÍCH bằng những tiêu chuẩn hành vi thích hợp&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; margin-left: 21pt;&quot;&gt;-&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; ĐÀO TẠO bằng những môn thể thao trọn đời&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; margin-left: 21pt;&quot;&gt;-&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; TỔ CHỨC thám hiểm ngoài trời&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; margin-left: 21pt;&quot;&gt;-&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; NUÔI DƯỠNG lòng tự trọng qua thành tựu thực tế&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;strong&gt;THỬ THÁCH bằng đội ngũ giáo viên nghiêm khắc&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;Với lớp học chỉ 10 em học sinh, SVA khuyến khích học sinh học giỏi nhờ từng em được chú ý và các chương trình hỗ trợ nên 100% được vào đại học.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;Giấy Chứng nhận của Ủy ban Quốc gia về tiêu chuẩn giảng dạy, Năng lực giảng dạy cấp tiểu bang, và nhiều năm kinh nghiệm đã hun đúc cho đội ngũ giáo viên sự tinh thông và sự cống hiến để thực hiện sứ mệnh tại SVA.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;em&gt;“Sự kết hợp giữa chương trình học tuyệt hảo với các môn thể thao đã tạo nền tảng cho con trai tôi trưởng thành và phát triển.” &lt;/em&gt;Phát biểu của một phụ huynh.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;strong&gt;&amp;nbsp;NUÔI DƯỠNG lòng tự trọng qua thành tựu thực tế&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;Tại Squaw Valley Academy, sự tận tụy của thầy cô giáo đã tạo ra cộng đồng có mối liên hệ đan xen chặt chẽ với nhau để nuôi dưỡng sự trưởng thành và phát triển của từng học sinh. Những vận động viên trượt tuyết Olympic, những giám đốc điều hành, những VĐV lướt ván tuyết và nhiều sinh viên tốt nghiệp bắt đầu thành công của họ bằng sự cấu thành và sự tự tin học được từ SVA.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;strong&gt;KHUYẾN KHÍCH bằng những tiêu chuẩn hành vi thích hợp&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;Chương trình chuẩn bị đại học và các khóa học định hướng cao cấp tại SVA được cộng nhận bởi hệ thống trường University of California. Tất cả các khóa học được giảng dạy bởi những giáo viên giàu kinh nghiệm và nhiệt tâm, luôn khơi dậy những khả năng tiềm tàng của từng em. SVA còn tổ chức dạy ban đêm để đảm bảo sự tiến bộ và thấm nhuần các kỷ năng học tập cho mỗi học sinh.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;strong&gt;ĐÀO TẠO bằng những môn thể thao trọn đời&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;SVA nhấn mạnh giáo dục học sinh toàn diện qua phát triển cơ thể và trí óc. Một trí óc khỏe mạnh là phải biết tìm hiểu, phân tích và cảm nhận nhanh. Một cơ thể khỏe mạnh là phải tráng kiện và dẻo dai.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;Học sinh chọn môn thể thao để chơi suốt đời và hoạt động ngoài trời. Những môn thể thao có thi đấu và không thi đấu giúp phát triển toàn diện bằng cách xây dựng ý thức cộng đồng, nuôi dưỡng phong cách lãnh đạo và tinh thần đồng đội, và thúc đẩy sự tự tin.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;Vào mùa đông, học sinh có dịp trượt tuyết và lướt ván tại Thung Lũng Squaw, nơi diễn ra Olympic Mùa Đông năm 1960.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px; text-align: center;&quot;&gt;&lt;img alt=&quot;&quot; height=&quot;472&quot; src=&quot;http://farm9.staticflickr.com/8221/8263236702_2b64f71a22_b.jpg&quot; style=&quot;border: 0px;&quot; width=&quot;708&quot; /&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;strong&gt;TỔ CHỨC thám hiểm ngoài trời&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;Chương trình ngoài trời dựa vào triết lý để học sinh hưởng lợi từ sự trải nghiệm thực tế. Rừng &lt;a href=&quot;http://www.khaiminh.edu.vn/showBookletItem.php?id=120##&quot; style=&quot;color: rgb(117, 117, 117); text-decoration: none;&quot;&gt;Lake Tahoe&lt;/a&gt; National Forest gần sát trường cho nhiều cơ hội này. Những Chuyến Giáo Dục Ngoài Trời vào Mùa Xuân và Mùa Thu là móc cho hoạt động ngoài trời. Hai lần trong năm, toàn bộ học sinh, giáo viên và nhân viên của trường chọn một trong những chuyến dã ngoại mạo hiểm như &lt;a href=&quot;http://www.khaiminh.edu.vn/showBookletItem.php?id=120##&quot; style=&quot;color: rgb(117, 117, 117); text-decoration: none;&quot;&gt;leo&lt;/a&gt; núi đá, khám phá hang động, đi xe đạp, lướt sóng, cỡi ngựa… Sự từng trải và sự trưởng thành trong cảm xúc có được từ những chuyến dã ngoại này giúp học sinh dễ thành công hơn ở đại học và trong cuộc sống.&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&lt;strong&gt;3. Chương trình đào tạo:&lt;/strong&gt;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;Để đảm bảo chất lượng đào tạo và phát huy được năng lực cá nhân học sinh một cách tốt nhất, các lớp học của trường Squaw Valley thường chỉ có từ 8 đến 12 học sinh. Đội ngũ giáo viên ở đây rất kiên nhẫn và giàu kinh nghiệm. Vào các ngày học trong tuần, học sinh còn được học tại hội trường vào các buổi tối dưới sự giám sát của một hoặc vài giáo viên. Trường cũng tổ chức các hoạt động ngoại khoá trong dịp cuối tuần. Tất cả các học sinh của trường đều tham gia các hoạt động ngoài trời như leo núi, trượt tuyết, cắm trại, đi bộ đường dài, tham quan bảo tàng…&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;Chương trình tiếng Anh cho sinh viên mới với sĩ số lớp khoảng 25 em. Chương trình học có lớp học nâng cao, lớp học danh dự ở trường. Khoá học bao gồm : số học, giải tích, sinh học, lịch sử, , vật lý, hoá học, tiếng Anh, tiếng Tây Ban Nha, kinh tế, nghệ thuật. Sĩ số lớp trung bình là 9 em, tỉ lệ gv/sv là 1 :6&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: auto; word-spacing: 0px; -webkit-text-stroke-width: 0px;&quot;&gt;&amp;nbsp;&lt;/p&gt;\r\n\r\n&lt;p style=&quot;color: rgb(117, 117, 117); font-family: ''Times New Roman'', Times, serif; font-size: 14px; font-style: normal; font-variant: normal; font-weight: normal; letter-spacing: normal; line-height: normal; orphans: auto; text-align: start; text-indent: 0